mapper.update:
UPDATE qa
SET reply = NULL, replyStatus = '답변준비중'
WHERE id = #{id}

mapper.select:
select q.*, u.name
from (select rank() over(order by id desc) as no,
    i.*
      from
          (select * from qa) i) q
         left join users u on q.user_id = u.id
where no between #{ start }
  and #{ end }
