mapper.update:
update users
set deleted = 1
where id = #{userId}

update users
set password = #{encPwd}
where id = #{userId}

mapper.select:
select p.*
from products p
left join product_likes on p.id = product_likes.product_id
where product_likes.user_id = #{userId}
order by id desc
limit #{pageSize} offset #{offset}

select count(*)
from product_likes
where user_id = #{userId}
